import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { collection, getDocs, doc, setDoc, getDoc } from "firebase/firestore";
import { auth, firestore } from "@/lib/firebase";
import { useAuth } from "@/lib/auth-context";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { MessageSquare, Loader2, Info, ShieldAlert } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { UserRole } from "@shared/schema";

export default function Register() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { refreshUser } = useAuth();
  const [loading, setLoading] = useState(false);
  const [checkingUsers, setCheckingUsers] = useState(true);
  const [hasExistingUsers, setHasExistingUsers] = useState<boolean | null>(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [displayName, setDisplayName] = useState("");

  // Check for existing users when component mounts
  useEffect(() => {
    const checkForExistingUsers = async () => {
      setCheckingUsers(true);
      try {
        const usersSnapshot = await getDocs(collection(firestore, "users"));
        setHasExistingUsers(!usersSnapshot.empty);
      } catch (error) {
        console.error("Error checking users:", error);
        setHasExistingUsers(null);
      } finally {
        setCheckingUsers(false);
      }
    };

    checkForExistingUsers();
  }, []);

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Check if there are existing users
      const usersSnapshot = await getDocs(collection(firestore, "users"));
      const isFirstUser = usersSnapshot.empty;

      // After first admin is created, registration is closed
      if (!isFirstUser) {
        toast({
          title: "Registration Closed",
          description: "Please contact your administrator to create an account for you.",
          variant: "destructive",
        });
        setLoading(false);
        return;
      }

      // Create Firebase auth user
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);

      // Create user document in Firestore with the user ID
      const userData = {
        id: userCredential.user.uid,
        email: email,
        displayName: displayName,
        role: UserRole.ADMIN,
        createdAt: Date.now(),
      };

      await setDoc(doc(firestore, "users", userCredential.user.uid), userData);

      // Verify the document was created
      const userDoc = await getDoc(doc(firestore, "users", userCredential.user.uid));

      if (!userDoc.exists()) {
        throw new Error("Failed to create user profile");
      }

      // Refresh the auth context to pick up the new user immediately
      await refreshUser();

      toast({
        title: "Admin Account Created!",
        description: "You are now the administrator of this TeamConnect instance.",
      });

      // Navigate to dashboard - auth context now has the user loaded
      setLocation("/");
    } catch (error: any) {
      toast({
        title: "Registration failed",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center space-y-4">
          <div className="w-16 h-16 rounded-lg bg-primary flex items-center justify-center mx-auto">
            <MessageSquare className="w-8 h-8 text-primary-foreground" />
          </div>
          <div>
            <CardTitle className="text-3xl font-semibold">
              {hasExistingUsers ? "Registration Closed" : "Create Account"}
            </CardTitle>
            <CardDescription className="mt-2">
              {checkingUsers 
                ? "Checking system status..."
                : hasExistingUsers === false
                ? "Set up your administrator account"
                : "Contact your administrator"}
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          {checkingUsers ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="w-8 h-8 animate-spin text-muted-foreground" />
            </div>
          ) : hasExistingUsers === false ? (
            <>
              <Alert className="mb-4 border-primary bg-primary/10">
                <Info className="w-4 h-4 text-primary" />
                <AlertDescription className="text-sm">
                  <strong>First User Setup:</strong> You will be registered as the administrator.
                  After setup, you can create accounts for other users.
                </AlertDescription>
              </Alert>

              <form onSubmit={handleRegister} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="displayName">Full Name</Label>
                  <Input
                    id="displayName"
                    type="text"
                    placeholder="John Doe"
                    value={displayName}
                    onChange={(e) => setDisplayName(e.target.value)}
                    required
                    data-testid="input-display-name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    placeholder="you@company.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    data-testid="input-email"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    type="password"
                    placeholder=""
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    minLength={6}
                    data-testid="input-password"
                  />
                  <p className="text-xs text-muted-foreground">
                    Must be at least 6 characters
                  </p>
                </div>
                <Button
                  type="submit"
                  className="w-full"
                  disabled={loading}
                  data-testid="button-register"
                >
                  {loading ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Creating Admin Account...
                    </>
                  ) : (
                    "Create Admin Account"
                  )}
                </Button>
              </form>

              <div className="mt-4 text-center">
                <p className="text-sm text-muted-foreground">
                  Already have an account?{" "}
                  <a href="/login" className="text-primary hover:underline" data-testid="link-login">
                    Sign In
                  </a>
                </p>
              </div>
            </>
          ) : (
            <>
              <Alert className="mb-4 border-destructive bg-destructive/10">
                <ShieldAlert className="w-4 h-4 text-destructive" />
                <AlertDescription className="text-sm">
                  <strong>Registration Disabled:</strong> Self-registration has been closed after the first administrator account was created.
                  This is a security measure to prevent unauthorized access.
                </AlertDescription>
              </Alert>
              <div className="space-y-3">
                <p className="text-sm text-muted-foreground text-center">
                  Please contact your TeamConnect administrator to create an account for you.
                </p>
                <Button
                  onClick={() => setLocation("/login")}
                  variant="outline"
                  className="w-full"
                  data-testid="button-back-to-login"
                >
                  Back to Sign In
                </Button>
              </div>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/users.tsx.content:
import { useState } from "react";
import { useAuth } from "@/lib/auth-context";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Users as UsersIcon, Plus, Loader2, Mail } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { collection, getDocs, doc, setDoc } from "firebase/firestore";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth, firestore } from "@/lib/firebase";
import { UserRole, type User } from "@shared/schema";
import { Skeleton } from "@/components/ui/skeleton";

export default function UsersPage() {
  const { currentUser } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [createUserOpen, setCreateUserOpen] = useState(false);
  const [newUserEmail, setNewUserEmail] = useState("");
  const [newUserPassword, setNewUserPassword] = useState("");
  const [newUserName, setNewUserName] = useState("");
  const [newUserRole, setNewUserRole] = useState<string>("");

  // Fetch all users
  const { data: users, isLoading } = useQuery({
    queryKey: ["/api/users"],
    queryFn: async () => {
      const usersSnapshot = await getDocs(collection(firestore, "users"));
      return usersSnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      })) as User[];
    },
  });

  const createUserMutation = useMutation({
    mutationFn: async () => {
      if (!newUserEmail || !newUserPassword || !newUserName || !newUserRole) {
        throw new Error("All fields are required");
      }

      // Note: In production, user creation should be done via Firebase Admin SDK on the backend
      // to avoid signing out the current admin/manager user. For this demo, we'll use a workaround.

      // Store current user's credentials to re-authenticate later if needed
      const currentUserEmail = currentUser?.email;

      // Create Firebase auth user
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        newUserEmail,
        newUserPassword
      );

      // Create user document in Firestore
      const userData: Omit<User, 'id'> = {
        email: newUserEmail,
        displayName: newUserName,
        role: newUserRole as any,
        createdAt: Date.now(),
        createdBy: currentUser?.id,
      };

      await setDoc(doc(firestore, "users", userCredential.user.uid), userData);

      // Sign out the newly created user (they'll need to login with their credentials)
      await auth.signOut();

      // Note: In a production app, the admin would remain signed in because 
      // user creation would happen server-side via Admin SDK

      return userCredential.user;
    },
    onSuccess: () => {
      toast({
        title: "User Created",
        description: "New user account has been created successfully",
      });
      setNewUserEmail("");
      setNewUserPassword("");
      setNewUserName("");
      setNewUserRole("");
      setCreateUserOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      queryClient.invalidateQueries({ queryKey: ["/api/users/count"] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to create user",
        description: error.message || "An error occurred",
        variant: "destructive",
      });
    },
  });

  const handleCreateUser = () => {
    createUserMutation.mutate();
  };

  const roleColors = {
    [UserRole.ADMIN]: "bg-primary text-primary-foreground",
    [UserRole.MANAGER]: "bg-chart-2 text-white",
    [UserRole.HR]: "bg-chart-3 text-white",
    [UserRole.EMPLOYEE]: "bg-chart-4 text-white",
    [UserRole.CLIENT]: "bg-muted text-muted-foreground",
  };

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .substring(0, 2);
  };

  // Determine allowed roles based on current user
  const getAllowedRoles = () => {
    if (currentUser?.role === UserRole.ADMIN) {
      return [UserRole.ADMIN, UserRole.MANAGER, UserRole.HR, UserRole.EMPLOYEE, UserRole.CLIENT];
    } else if (currentUser?.role === UserRole.MANAGER) {
      return [UserRole.HR, UserRole.EMPLOYEE];
    }
    return [];
  };

  const allowedRoles = getAllowedRoles();

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-semibold" data-testid="page-title">User Management</h1>
          <p className="text-muted-foreground mt-2">
            Create and manage team member accounts
          </p>
        </div>
        <Dialog open={createUserOpen} onOpenChange={setCreateUserOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-user">
              <Plus className="w-4 h-4 mr-2" />
              Create User
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New User</DialogTitle>
              <DialogDescription>
                Add a new team member to the system
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="userName">Full Name</Label>
                <Input
                  id="userName"
                  placeholder="John Doe"
                  value={newUserName}
                  onChange={(e) => setNewUserName(e.target.value)}
                  data-testid="input-user-name"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="userEmail">Email</Label>
                <Input
                  id="userEmail"
                  type="email"
                  placeholder="john@company.com"
                  value={newUserEmail}
                  onChange={(e) => setNewUserEmail(e.target.value)}
                  data-testid="input-user-email"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="userPassword">Password</Label>
                <Input
                  id="userPassword"
                  type="password"
                  placeholder=""
                  value={newUserPassword}
                  onChange={(e) => setNewUserPassword(e.target.value)}
                  data-testid="input-user-password"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="userRole">Role</Label>
                <Select value={newUserRole} onValueChange={setNewUserRole}>
                  <SelectTrigger id="userRole" data-testid="select-user-role">
                    <SelectValue placeholder="Select role" />
                  </SelectTrigger>
                  <SelectContent>
                    {allowedRoles.map((role) => (
                      <SelectItem key={role} value={role}>
                        {role.charAt(0).toUpperCase() + role.slice(1)}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
            <DialogFooter>
              <Button
                onClick={handleCreateUser}
                disabled={createUserMutation.isPending || !newUserEmail || !newUserPassword || !newUserName || !newUserRole}
                data-testid="button-submit-create-user"
              >
                {createUserMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating...
                  </>
                ) : (
                  "Create User"
                )}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <UsersIcon className="w-5 h-5" />
            Team Members
          </CardTitle>
          <CardDescription>
            All registered users in the system
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-3">
              {[1, 2, 3, 4].map((i) => (
                <div key={i} className="flex items-center gap-4 p-4 border rounded-md">
                  <Skeleton className="w-12 h-12 rounded-full" />
                  <div className="flex-1 space-y-2">
                    <Skeleton className="h-4 w-32" />
                    <Skeleton className="h-3 w-48" />
                  </div>
                  <Skeleton className="h-6 w-20" />
                </div>
              ))}
            </div>
          ) : !users || users.length === 0 ? (
            <div className="text-center py-12">
              <UsersIcon className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No users found</p>
            </div>
          ) : (
            <div className="space-y-3">
              {users.map((user) => (
                <div
                  key={user.id}
                  className="flex items-center gap-4 p-4 border rounded-md hover-elevate"
                  data-testid={`user-card-${user.id}`}
                >
                  <Avatar className="w-12 h-12">
                    <AvatarImage src={user.photoURL} />
                    <AvatarFallback>{getInitials(user.displayName)}</AvatarFallback>
                  </Avatar>
                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <p className="font-medium">{user.displayName}</p>
                      <Badge
                        className={`text-xs ${roleColors[user.role]}`}
                        data-testid={`user-role-${user.id}`}
                      >
                        {user.role}
                      </Badge>
                    </div>
                    <div className="flex items-center gap-1 mt-1">
                      <Mail className="w-3 h-3 text-muted-foreground" />
                      <p className="text-sm text-muted-foreground">{user.email}</p>
                    </div>
                    {user.createdAt && (
                      <p className="text-xs text-muted-foreground mt-1">
                        Joined {new Date(user.createdAt).toLocaleDateString()}
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

